import React, { useState } from "react";

const DropDown = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    const { inputList: options, 
            optionClicked,
        } = props;

    const optionClickHandler = (e) => {
        console.log(e);
        optionClicked(e);
    }
    return (
        <div>
            <div>
            <div onClick={() => setIsOpen(prevState => !prevState)}>User List</div>
            {
                isOpen && (
                    <div>
                    {options.map((option) => (
                    <li onClick={() => optionClickHandler(option)} value={option.id}>{option.name}</li>
                ))}
                    </div>
                )
            }
            </div>
        </div>
    )
}

export default DropDown;
