import React, { useEffect, useState } from 'react';
import './App.css';
import KeyPressShortCut from './component/KeyPress/KeyPress';

function App() {
  const [info, setInfo] = useState([]);
  useEffect(() => {
    const obj = {
      title: 'Component A',
      combo: 'Shift G',
      des: 'Toogle color to Yellow',
    }

    setInfo(prevState => [...prevState, obj]);
  }, []);

  const handleA = () => {
    const a = document.getElementsByClassName('componentB');

  }
  return (
    <React.Fragment>
      <section>
      <div className='container'>
        <KeyPressShortCut title="A" description="Toggle color of A to yellow" callBack={handleA}/>
      <div className='componentA'>B</div>
      </div>

      <div className='container'>
      <div className='componentB'>C</div>
      <div className='helper'>
        {info && info.map((obj, idx) => (
          <div className='info_container' key={idx}>
            <div className='title'>{`${obj.title} : `}</div>
            <div className='combo'>{`${obj.combo} : `}</div>
            <div className='description'>{`${obj.des}`}</div>
          </div>
        ))}
      </div>
      </div>
      </section>
    </React.Fragment>
  );
}

export default App;
