import React, { useEffect } from "react";
const KeyPressShortCut = (props) => {

    useEffect(() => {
        const obj = {
          title: props.title,
          combo: props.combo,
          des: props.description,
        }

        props.setInfo(obj);
      }, []);

    useEffect(() => {
        document.onkeydown = KeyPress;
    }, [])
    const KeyPress = (e) => {
        var evtobj = window.event ? window.event : e;
        const { combo } = props;
        const arr = combo.split(' ');
        console.log(evtobj.shiftKey, evtobj.key);
        let condition = false;
        arr.forEach((item) => {
            if (!condition) {
                condition = evtobj[item];
                return;
            }
            condition = evtobj[item] && condition;
        })
        if (evtobj.key == 'G' && evtobj.shiftKey) alert("shift+g");
        }
    return (
        <h1 onClick={() => props.callBack(props.color)}> 
            {props.title}
        </h1>
    )
}

export default KeyPressShortCut;
