import React, { useEffect } from "react";
const KeyPressShortCut = (props) => {

    useEffect(() => {
        const obj = {
          title: props.title,
          combo: props.combo,
          des: props.description,
        }

        props.setInfo(obj);
      }, []);

    useEffect(() => {
        console.log(props.combo)
        document.onkeyup = KeyPress;
    }, [])
    const KeyPress = (e) => {

        var evtobj = window.event ? window.event : e;
        const { combo } = props;
        const keySplit = combo.split(' ');
        console.log(keySplit, e.key, e.shiftKey);
        let condition = false;
        keySplit.forEach((item, index) => {
        if (index === keySplit.length -1) {
            condition = condition && (evtobj.key === item || (evtobj.key === (item ? item.toUppercase() : item)));
          return;
        }
        if (!condition) {
        condition = evtobj[item];
        return;
        }
        condition = evtobj[item] && condition;
        })
        if (condition) alert("you made it");
        }

        console.log(props.combo)
    return (
        <h1 onClick={() => props.callBack(props.color, props.combo)}> 
            {props.title}
        </h1>
    )
}

export default KeyPressShortCut;
