import React, { useEffect, useState } from 'react';
import './App.css';
import KeyPressShortCut from './component/KeyPress/KeyPress';

function App() {
  const [info, setInfo] = useState([]);

  const handleA = (color) => {
    const a = document.getElementById('A');

    if (a.style.background === 'red') {
      a.style.background = 'blue';
    } else {
      a.style.background = color;
    }
  }

  const handleB = (color) => {
    const a = document.getElementById('B');

    if (a.style.background === 'yellow') {
      a.style.background = 'blue';
    } else {
      a.style.background = color;
    }
  }

  const handleC = (color) => {
    const a = document.getElementById('C');

    if (a.style.background === 'green') {
      a.style.background = 'blue';
    } else {
      a.style.background = color;
    }
  }

  const handleInfo = (obj) => {
    setInfo(prev => [...prev, obj])
  }

  return (
    <React.Fragment>
      <section>
      <div className='container'>
        <div className='componentA' id='A'>
        <KeyPressShortCut title="A" description="Toggle color of A to yellow" callBack={handleA} color='red' combo='shift r' setInfo={handleInfo}/>
        </div>
      <div className='componentA' id='B'>
        <KeyPressShortCut title="B" description="Toggle color of A to yellow" callBack={handleB} color='black' combo='shift b' setInfo={handleInfo}/>
      </div>
      </div>
      <div className='container'  id='C'>
      <div className='componentA'>
       <KeyPressShortCut title="C" description="Toggle color of A to yellow" callBack={handleC} color='green' combo='ctrl shift g' setInfo={handleInfo}/>
      </div>
      <div className='helper'>
        {info && info.map((obj, idx) => (
          <div className='info_container' key={idx}>
            <div className='title'>{`${obj.title} : `}</div>
            <div className='combo'>{`${obj.combo} : `}</div>
            <div className='description'>{`${obj.des}`}</div>
          </div>
        ))}
      </div>
      </div>
      </section>
    </React.Fragment>
  );
}

export default App;
